mapper.insert:
insert into MyBasket (mycode, mystuck, myprice, myuuid, myshipPay, mypname, myamount , myfnames, myeno, mybrand)
select code as mycode, stuck as mystuck, dcprice as myprice, #{userid} as myuuid, shipPay as myshipPay, title as mypname, #{mbamount} as myamount, fnames as myfnames, eno as myeno, brand as mybrand from eshop
where code = #{code};

mapper.select:
select mbno, mycode, mystuck, myprice, myuuid, myshipPay, mypname, myamount, myfnames, myeno from MyBasket
where myuuid = #{userid}
order by mbno desc;

select mbno, mycode, mystuck, myprice, myuuid, myshipPay, mypname, myamount, myfnames, myeno from MyBasket
where myuuid = #{userid}
order by mbno desc;
