mapper.insert:
insert into MyBasket (mycode, mystuck, myprice, myuuid, myshipPay, mypname, myamount)
select code as mycode, stuck as mystuck, dcprice as myprice, #{userid} as myuuid, shipPay as myshipPay, title as mypname, ${salesCnt} as myamount from eshop
where code = #{code};

mapper.select:
select mbno, mycode, mystuck, myprice, myuuid, myshipPay, mypname, myshipPay from MyBasket
where myuuid = #{userid}
order by mbno desc;
